	class StarterSite extends TimberSite {

		function __construct(){
			add_theme_support('post-formats');
			add_theme_support('post-thumbnails');
			set_post_thumbnail_size( 1920, 600, true ); 
			add_image_size('home_thumb', 420, 372, true);
			add_image_size('about_thumb', 420, 220, true);
	    add_image_size('shows_thumb', 620, 349, true);
			add_image_size('show_main_thumb', 940, 460, false);
			add_theme_support('menus');
			add_filter('timber_context', array($this, 'add_to_context'));
			add_filter('get_twig', array($this, 'add_to_twig'));
			add_action('init', array($this, 'register_post_types'));
			add_action('init', array($this, 'register_taxonomies'));
			add_action('wp_enqueue_scripts', array($this, 'register_scripts'));
			parent::__construct();
		}

		function register_scripts(){
			wp_enqueue_script('template', get_template_directory_uri() . '/theme/compressed/template.min.js', array( 'jquery' ), '1.0.0', true);
		}

